using namespace std;

bool hasCloseElements(const vector<float>& numbers, float threshold) {
    if (numbers.size() < 2) {
        return false;
    }
    vector<float> sorted_numbers = numbers;
    sort(sorted_numbers.begin(), sorted_numbers.end());
    for (size_t i = 0; i < sorted_numbers.size() - 1; ++i) {
        if (sorted_numbers[i+1] - sorted_numbers[i] < threshold) {
            return true;
        }
    }
    return false;
}

#undef NDEBUG
