vector<string> extractParenthesesGroups(const string& paren_string) {
    // Remove all spaces from the input string
    string cleaned_str;
    for (char c : paren_string) {
        if (c != ' ') {
            cleaned_str += c;
        }
    }

    vector<string> result;
    int current_start = 0;
    int balance = 0;

    for (int i = 0; i < cleaned_str.size(); ++i) {
        char c = cleaned_str[i];
        if (c == '(') {
            balance++;
        } else { // assuming it's ')'
            balance--;
        }

        if (balance == 0) {
            string group = cleaned_str.substr(current_start, i - current_start + 1);
            result.push_back(group);
            current_start = i + 1;
        }
    }

    return result;
}

