#include <sstream>

using namespace std;

vector<int> parse_nested_parens(string paren_string) {
    vector<int> result;
    stringstream ss(paren_string);
    string group;

    while (ss >> group) {
        int current_depth = 0;
        int max_depth = 0;

        for (char c : group) {
            if (c == '(') {
                current_depth++;
                if (current_depth > max_depth) {
                    max_depth = current_depth;
                }
            } else { // ')'
                current_depth--;
            }
        }

        result.push_back(max_depth);
    }

    return result;
}

