#include <string>
using namespace std;

string string_xor(string a, string b) {
    int len_a = a.size();
    int len_b = b.size();

    int max_len = max(len_a, len_b);

    // Pad with leading zeros to make both strings of length max_len
    if (len_a < max_len) {
        a = string(max_len - len_a, '0') + a;
    }
    if (len_b < max_len) {
        b = string(max_len - len_b, '0') + b;
    }

    string result;
    for (int i = 0; i < max_len; ++i) {
        // Compute XOR of current bits
        if (a[i] != b[i]) {
            result += '1';
        } else {
            result += '0';
        }
    }

    return result;
}

#undef NDEBUG
#include<assert.h>
int main(){
    assert (string_xor("111000", "101010") == "010010");
     assert (string_xor("1", "1") == "0");
    assert (string_xor("0101", "0000") == "0101");

}

