vector<int> intersperse(vector<int> numbers, int delimeter) {
    vector<int> result;
    if (numbers.empty()) {
        return result;
    }
    int n = numbers.size();
    for (int i = 0; i < n - 1; ++i) {
        result.push_back(numbers[i]);
        result.push_back(delimeter);
    }
    result.push_back(numbers.back());
    return result;
}

