string longest(vector<string> strings) {
    if (strings.empty()) {
        return "";
    }

    string result = strings[0];
    int max_len = result.length();

    for (size_t i = 1; i < strings.size(); ++i) {
        const string& s = strings[i];
        if (s.length() > max_len) {
            max_len = s.length();
            result = s;
        }
    }

    return result;
}

