using namespace std;

int countDistinctCharacters(string str) {
    bool seen[256] = {false};
    int count = 0;
    for (char c : str) {
        if (c >= 'A' && c <= 'Z') {
            c = c - 'A' + 'a';
        }
        int index = static_cast<int>(c);
        if (!seen[index]) {
            seen[index] = true;
            count++;
        }
    }
    return count;
}

