vector<float> findClosestPair(const vector<float>& numbers) {
    if (numbers.size() < 2) {
        // Not enough elements to form a pair
        return {};
    }

    vector<float> sorted(numbers);
    sort(sorted.begin(), sorted.end());

    float min_diff = numeric_limits<float>::max();
    float first, second;

    for (size_t i = 0; i < sorted.size() - 1; ++i) {
        float diff = sorted[i+1] - sorted[i];
        if (diff < min_diff) {
            min_diff = diff;
            first = sorted[i];
            second = sorted[i+1];
        }
    }

    return {first, second};
}

