    if (len_sub == 0 || len_str < len_sub) {
        return 0;
    }

    int count = 0;
    for (int i = 0; i <= len_str - len_sub; ++i) {
        if (str.substr(i, len_sub) == substring) {
            ++count;
        }
    }

    return count;
}

