vector<int> factor(int n) {
    vector<int> res;
    if (n == 0)
        return res; // 0 has no prime factors

    bool isNegative = false;
    if (n < 0) {
        isNegative = true;
        n = abs(n);
    }

    if (n == 1) {
        if (isNegative) {
            res.push_back(-1);
        }
        return res;
    }

    for (int i = 2; i * i <= n; ++i) {
        while (n % i == 0) {
            res.push_back(i);
            n /= i;
        }
    }

    if (n > 1)
        res.push_back(n);

    if (isNegative && !res.empty()) {
        res.insert(res.begin(), -1);
    }

    return res;
}

