vector<float> rescale_unity(vector<float> numbers) {
    if (numbers.empty()) {
        return vector<float>();
    }

    float min_val = *min_element(numbers.begin(), numbers.end());
    float max_val = *max_element(numbers.begin(), numbers.end());

    float range = max_val - min_val;

    vector<float> result;
    for (float x : numbers) {
        if (range == 0) {
            result.push_back(0.0f);
        } else {
            float scaled = (x - min_val) / range;
            result.push_back(scaled);
        }
    }

    return result;
}

