string sort_numbers(string numbers) {
    vector<string> tokens;
    string token;
    istringstream iss(numbers);
    while (iss >> token) {
        tokens.push_back(token);
    }

    map<string, int> wordToNum = {
        {"zero", 0},
        {"one", 1},
        {"two", 2},
        {"three", 3},
        {"four", 4},
        {"five", 5},
        {"six", 6},
        {"seven", 7},
        {"eight", 8},
        {"nine", 9}
    };

    vector<int> nums;
    for (const string &word : tokens) {
        nums.push_back(wordToNum[word]);
    }

    sort(nums.begin(), nums.end());

    const vector<string> numWords = {"zero", "one", "two", "three", "four", "five", "six", "seven", "eight", "nine"};

    vector<string> sortedTokens;
    for (int n : nums) {
        sortedTokens.push_back(numWords[n]);
    }

    string result;
    if (!sortedTokens.empty()) {
        result = sortedTokens[0];
        for (size_t i = 1; i < sortedTokens.size(); ++i) {
            result += " " + sortedTokens[i];
        }
    }

    return result;
}

