bool is_prime(long long n) {
    if (n <= 1)
        return false;
    if (n <= 3)
        return true; // 2 and 3 are primes
    if (n % 2 == 0)
        return false;
    for (long long i = 3; i * i <= n; i += 2) {
        if (n % i == 0)
            return false;
    }
    return true;
}

