string decode_cyclic(const string& s) {
    string result;
    int n = s.length();
    for (int i = 0; i < n; i += 3) {
        if (i + 2 < n) { // Check if there are at least three characters
            // Rotate right: last character moves to the front
            result += s[i+2];
            result += s.substr(i, 2); // Add first two characters
        } else {
            // If less than three characters left, add them as is
            result += s.substr(i);
        }
    }
    return result;
}

