bool is_prime(long long num) {
    if (num <= 1)
        return false;
    if (num == 2)
        return true;
    if (num % 2 == 0)
        return false;
    for (long long i = 3; i * i <= num; i += 2) {
        if (num % i == 0)
            return false;
    }
    return true;
}

int prime_fib(int n) {
    long long a = 0, b = 1;
    int count = 0;

    while (true) {
        long long c = a + b;
        if (is_prime(c)) {
            count++;
            if (count == n)
                return c; // Assuming c fits in an int
        }
        a = b;
        b = c;
    }
}

