string intToBase(int num, int base) {
    if (num == 0)
        return "0";

    bool isNegative = false;
    if (num < 0) {
        isNegative = true;
        num = -num;
    }

    string result;
    while (num > 0) {
        int rem = num % base;
        if (rem >= 10)
            result += ('A' + rem - 10);
        else
            result += to_string(rem);
        num /= base;
    }

    reverse(result.begin(), result.end());
    return isNegative ? "-" + result : result;
}

