float median(vector<float> l) {
    sort(l.begin(), l.end());
    int n = l.size();
    if (n % 2 == 1) {
        return l[n / 2];
    } else {
        float mid1 = l[(n / 2) - 1];
        float mid2 = l[n / 2];
        return (mid1 + mid2) / 2.0f;
    }
}

