int countVowels(const string& s) {
    int count = 0;
    if (s.empty()) return 0;

    // Check all characters except the last one for vowels a, e, i, o, u
    for (size_t i = 0; i < s.size() - 1; ++i) {
        char c = tolower(s[i]);
        if (c == 'a' || c == 'e' || c == 'i' || c == 'o' || c == 'u') {
            count++;
        }
    }

    // Check the last character for vowels a, e, i, o, u or y
    char last = tolower(s.back());
    if (last == 'a' || last == 'e' || last == 'i' || last == 'o' || last == 'u' || last == 'y') {
        count++;
    }

    return count;
}

