string circularShift(int x, int shift) {
    string s = to_string(abs(x));
    int len = s.size();

    if (shift > 0 && len > 0) {
        shift %= len; // To handle cases where shift is larger than the length
        reverse(s.begin(), s.end());
        rotate(s.begin(), s.begin() + shift, s.end());
        reverse(s.begin(), s.end());
    }

    return s;
}

