vector<int> pluck(vector<int> arr) {
    if (arr.empty()) {
        return {};
    }

    int min_even = INT_MAX;
    int index = -1;

    for (int i = 0; i < arr.size(); ++i) {
        int num = arr[i];
        if (num % 2 == 0) { // Check if the number is even
            if (num < min_even) {
                min_even = num;
                index = i;
            }
        }
    }

    if (index == -1) {
        return {};
    } else {
        return {min_even, index};
    }
}

