bool will_it_fly(vector<int> q, int w) {
    // Check if the vector is a palindrome
    for (int i = 0; i < q.size() / 2; ++i) {
        if (q[i] != q[q.size() - 1 - i]) {
            return false;
        }
    }

    // Calculate the sum of elements
    int sum = 0;
    for (int num : q) {
        sum += num;
    }

    // Check if the sum is within the allowed weight
    return sum <= w;
}

