string decimal_to_binary(int decimal) {
    if (decimal == 0) {
        return "db0db";
    }

    string binary_str;
    while (decimal > 0) {
        int rem = decimal % 2;
        binary_str += (rem == 0) ? '0' : '1';
        decimal /= 2;
    }

    reverse(binary_str.begin(), binary_str.end());

    return "db" + binary_str + "db";
}

