bool prime_length(string str) {
    int len = str.length();

    if (len <= 1) {
        return false;
    }
    if (len == 2) {
        return true;
    }
    if (len % 2 == 0) {
        return false;
    }

    for (int i = 3; i * i <= len; i += 2) {
        if (len % i == 0) {
            return false;
        }
    }

    return true;
}

