using namespace std;

string solve(int N) {
    int sum = 0;
    if (N == 0) {
        return "0";
    }
    int temp = N;
    while (temp > 0) {
        sum += temp % 10;
        temp /= 10;
    }

    string binary;
    if (sum == 0) {
        return "0";
    }

    while (sum > 0) {
        int rem = sum % 2;
        binary += to_string(rem);
        sum /= 2;
    }
    reverse(binary.begin(), binary.end());

    return binary;
}

