vector<vector<int>> collectAndSortCoordinates(const vector<vector<int>>& grid) {
    vector<vector<int>> ones;
    for (int i = 0; i < grid.size(); ++i) {
        for (int j = 0; j < grid[i].size(); ++j) {
            if (grid[i][j] == 1) {
                ones.push_back({i, j});
            }
        }
    }
    sort(ones.begin(), ones.end(), [](const vector<int>& a, const vector<int>& b) {
        if (a[0] != b[0]) {
            return a[0] < b[0];
        } else {
            return a[1] > b[1];
        }
    });
    return ones;
