vector<string> splitIntoSentences(const string& s) {
    vector<string> sentences;
    string current;
    for (char c : s) {
        if (c == '.' || c == '!' || c == '?') { 
            sentences.push_back(current);
            current.clear();
        } else {
            current += c;
        }
    }
    if (!current.empty()) {
        sentences.push_back(current);
    }
    return sentences;
}

vector<string> splitIntoWords(const string& s) {
    vector<string> words;
    stringstream ss(s);
    string word;
    while (ss >> word) { 
        words.push_back(word);
    }
    return words;
}

int countFirstWordI(const string& s) {
    int count = 0;
    vector<string> sentences = splitIntoSentences(s);
    for (const string& sent : sentences) {
        if (sent.empty()) continue; // skip empty sentences
        vector<string> words = splitIntoWords(sent);
        if (!words.empty() && words[0] == "I") {
            count++;
        }
    }
    return count;
}

