string encrypt(string s) {
    string result;
    for (char c : s) {
        if (c >= 'a' && c <= 'z') {
            int shifted = (c - 'a' + 4) % 26;
            result += (shifted + 'a');
        } else {
            result += c;
        }
    }
    return result;
}

