vector<int> sort_array(vector<int> array) {
    vector<int> result = array;

    if (result.size() <= 1) {
        return result;
    }

    int first = result[0];
    int last = result.back();
    int sum = first + last;

    if (sum % 2 == 1) { // Odd sum
        sort(result.begin(), result.end());
    } else { // Even sum
        sort(result.begin(), result.end(), greater<int>());
    }

    return result;
}

