using namespace std;

bool checkMap(const map<string, string>& m) {
    bool isLower = false;
    bool isUpper = false;

    for (const auto& pair : m) {
        const string& key = pair.first;
        if (key.empty()) {
            continue; // Skip empty keys
        }

        bool currentAllLower = true;
        bool currentAllUpper = true;

        for (char c : key) {
            if (!islower(c)) {
                currentAllLower = false;
            }
            if (!isupper(c)) {
                currentAllUpper = false;
            }
        }

        if (!currentAllLower && !currentAllUpper) {
            return false; // Mixed case in a single key
        } else if (currentAllLower) {
            isLower = true;
        } else {
            isUpper = true;
        }
    }

    if (isLower && isUpper) {
        return false; // Some keys are lower, others upper
    } else {
        // If at least one key was processed and it's either all lower or all upper.
        if (isLower || isUpper) {
            return true;
        } else {
            // All keys were empty strings
            return true;
        }
    }
}

