int nearestInteger(std::string s) {
    double num = stod(s);
    int lower = static_cast<int>(floor(num));
    int upper = static_cast<int>(ceil(num));

    if (lower == upper) {
        return lower;
    }

    double d_lower = num - lower;
    double d_upper = upper - num;

    if (d_lower < d_upper) {
        return lower;
    } else if (d_upper < d_lower) {
        return upper;
    } else {
        // Equidistant case
        if (num >= 0) {
            return upper;
        } else {
            return lower;
        }
    }
}

