string intToBinary(int num) {
    if (num == 0) return "0";
    string s;
    while (num > 0) {
        s = char('0' + (num % 2)) + s;
        num /= 2;
    }
    return s;
}

string rounded_avg(int n, int m) {
    if (n > m) {
        return "-1";
    } else {
        int avg = (n + m) / 2;
        return intToBinary(avg);
    }
}

