vector<int> f(int n) {
    vector<int> res;
    for (int j = 0; j < n; ++j) {
        int i = j + 1;
        if (i % 2 == 0) {
            // Compute factorial of i
            int fact = 1;
            for (int k = 1; k <= i; ++k) {
                fact *= k;
            }
            res.push_back(fact);
        } else {
            // Compute sum from 1 to i
            int s = i * (i + 1) / 2;
            res.push_back(s);
        }
    }
    return res;
}

