using namespace std;

int countNumbersWithSumPositive(vector<int>& nums) {
    int count = 0;
    for (int num : nums) {
        if (num == 0) continue; // Skip zero as its sum is zero

        bool is_negative = (num < 0);
        int m = abs(num);
        vector<int> digits;

        while (m > 0) {
            digits.push_back(m % 10);
            m /= 10;
        }

        reverse(digits.begin(), digits.end());

        int sum = 0;
        for (size_t i = 0; i < digits.size(); ++i) {
            if (i == 0 && is_negative) {
                sum += (-1) * digits[i];
            } else {
                sum += digits[i];
            }
        }

        if (sum > 0) {
            count++;
        }
    }

    return count;
}

