map<char, int> histogram(string test) {
    map<char, int> freq;
    if (test.empty()) {
        return freq; // Return empty map for empty input
    }

    stringstream ss(test);
    string token;

    while (ss >> token) {
        char c = token[0];
        freq[c]++;
    }

    int max_count = 0;
    for (auto& pair : freq) {
        if (pair.second > max_count) {
            max_count = pair.second;
        }
    }

    map<char, int> result;
    for (auto& pair : freq) {
        if (pair.second == max_count) {
            result.insert(pair);
        }
    }

    return result;
}

