int max_fill(vector<vector<int>> grid, int capacity) {
    int total = 0;
    for (auto &row : grid) {
        int sum = 0;
        for (int num : row) {
            sum += num;
        }
        if (sum == 0) continue;
        int ops = (sum + capacity - 1) / capacity;
        total += ops;
    }
    return total;
}

