bool valid_ate(const string& s) {
    if (s.length() != 10 || s[2] != '/' || s[5] != '/') return false;
    string day = s.substr(0,2);
    string month = s.substr(3,2);
    string year = s.substr(6,4);
    for (char c : day) if (!isdigit(c)) return false;
    for (char c : month) if (!isdigit(c)) return false;
    for (char c : year) if (!isdigit(c)) return false;
    int d = stoi(day), m = stoi(month), y = stoi(year);
    if (m < 1 || m > 12) return false;
    bool leap = (y % 400 == 0) ? true : ((y % 100 != 0 && y % 4 == 0) ? true : false);
    int max_day = 31;
    switch(m) {
        case 2: max_day = leap ? 29 : 28; break;
        case 4: case 6: case 9: case 11: max_day = 30; break;
        default: max_day = 31; break;
    }
    return d >= 1 && d <= max_day;
}

