vector<string> split_ones(const string& s) {
    vector<string> result;
    string current;
    for (char c : s) {
        if (c == ',') {
            result.push_back(current);
            current.clear();
        } else {
            current += c;
        }
    }
    if (!current.empty()) {
        result.push_back(current);
    }
    return result;
}

vector<string> processString(const string& txt) {
    bool has_whitespace = false;
    for (char c : txt) {
        if (isspace(c)) {
            has_whitespace = true;
            break;
        }
    }

    if (has_whitespace) {
        vector<string> result;
        istringstream iss(txt);
        string word;
        while (iss >> word) {
            result.push_back(word);
        }
        return result;
    } else if (txt.find(',') != string::npos) {
        return split_ones(txt);
    } else {
        int count = 0;
        for (char c : txt) {
            if (c >= 'a' && c <= 'z') {
                if ((int)c % 2 == 1) {
                    count++;
                }
            }
        }
        return vector<string>{to_string(count)};
    }
}

