using namespace std;

bool isSortedAndValid(const vector<int>& lst) {
    if (lst.empty()) return true;
    int n = lst.size();
    if (n == 1) return true;

    int prev = lst[0];
    int count = 1;

    for (int i = 1; i < n; ++i) {
        if (lst[i] < prev) {
            return false;
        } else if (lst[i] == prev) {
            count++;
            if (count > 2) {
                return false;
            }
        } else { // lst[i] > prev
            prev = lst[i];
            count = 1;
        }
    }

    return true;
}

