#include <vector>
using namespace std;

int prod_signs(vector<int> arr) {
    if (arr.empty()) {
        return -32768;
    }

    bool has_zero = false;
    for (int num : arr) {
        if (num == 0) {
            has_zero = true;
            break;
        }
    }
    if (has_zero) {
        return 0;
    }

    int sum_magnitude = 0;
    int product_signs = 1;
    for (int num : arr) {
        sum_magnitude += abs(num);
        if (num > 0) {
            product_signs *= 1;
        } else {
            product_signs *= -1;
        }
    }

    return sum_magnitude * product_signs;
}

