#include <stdio.h>

int digits(int n) {
    int product = 1;
    bool hasOdd = false;

    while (n > 0) {
        int digit = n % 10;
        if (digit % 2 != 0) { // Check if the digit is odd
            product *= digit;
            hasOdd = true;
        }
        n /= 10; // Move to the next digit
    }

    return hasOdd ? product : 0;
}

