vector<int> largest_ smallest_integers(vector<int> lst) {
    vector<int> negatives;
    vector<int> positives;

    for (int num : lst) {
        if (num < 0) {
            negatives.push_back(num);
        } else if (num > 0) {
            positives.push_back(num);
        }
    }

    int a = 0; // Default to 0 if no negatives
    if (!negatives.empty()) {
        a = *max_element(negatives.begin(), negatives.end());
    }

    int b = 0; // Default to 0 if no positives
    if (!positives.empty()) {
        b = *min_element(positives.begin(), positives.end());
    }

    return {a, b};
}

