using namespace boost;

double get_value(any x) {
    if (x.type() == typeid(int)) {
        return any_cast<int>(x);
    } else if (x.type() == typeid(float)) {
        return any_cast<float>(x);
    } else if (x.type() == typeid(double)) {
        return any_cast<double>(x);
    } else if (x.type() == typeid(std::string)) {
        std::string s = any_cast<std::string>(x);
        std::replace(s.begin(), s.end(), ',', '.');
        size_t idx;
        double d = stod(s, &idx);
        return d;
    } else {
        throw std::invalid_argument("Invalid type");
    }
}

any compare_one(any a, any b) {
    const double epsilon = 1e-9;

    double val_a = get_value(a);
    double val_b = get_value(b);

    if (std::abs(val_a - val_b) < epsilon) {
        return std::string("None");
    } else if (val_a > val_b) {
        return a;
    } else {
