long long compute_gcd(long long a, long long b) {
    while (b != 0) {
        long long temp = b;
        b = a % b;
        a = temp;
    }
    return a;
}

bool isIntegerProduct(string x, string n) {
    // Split the strings into numerators and denominators
    size_t slash_x = x.find('/');
    long long a = stoll(x.substr(0, slash_x));
    long long b = stoll(x.substr(slash_x + 1));

    size_t slash_n = n.find('/');
    long long c = stoll(n.substr(0, slash_n));
    long long d = stoll(n.substr(slash_n + 1));

    // Compute the product's numerator and denominator
    long long ac = a * c;
    long long bd = b * d;

    // Compute GCD of ac and bd
    long long gcd_val = compute_gcd(ac, bd);

    // Simplify the fraction
    long long simplified_den = bd / gcd_val;

    return (simplified_den == 1);
}


