bool is_prime(int n) {
    if (n <= 1)
        return false;
    if (n == 2)
        return true;
    if (n % 2 == 0)
        return false;
    for (int i = 3; i * i <= n; i += 2) {
        if (n % i == 0)
            return false;
    }
    return true;
}

string words_in_sentence(string sentence) {
    vector<string> words;
    istringstream iss(sentence);
    string word;
    while (iss >> word) {
        words.push_back(word);
    }

    vector<string> result;
    for (auto& w : words) {
        int len = w.length();
        if (is_prime(len)) {
            result.push_back(w);
        }
    }

    string output;
    for (size_t i = 0; i < result.size(); ++i) {
        if (i != 0) {
            output += " ";
        }
        output += result[i];
    }
    return output.empty() ? "" : output;
}

