vector<string> getPlanetsBetween(string planet1, string planet2) {
    // Define the order of planets from closest to farthest from the sun
    static const vector<string> planets = {"Mercury", "Venus", "Earth", "Mars", 
                                           "Jupiter", "Saturn", "Uranus", "Neptune"};

    // Create a map for quick lookup of planet indices
    static unordered_map<string, int> planetIndex;
    if (planetIndex.empty()) {
        for (int i = 0; i < planets.size(); ++i) {
            planetIndex[planets[i]] = i;
        }
    }

    // Check if both planets exist in the predefined list
    auto it1 = planetIndex.find(planet1);
    auto it2 = planetIndex.find(planet2);
    if (it1 == planetIndex.end() || it2 == planetIndex.end()) {
        return {};
    }

    int index1 = it1->second;
    int index2 = it2->second;

    // Determine the start and end indices
    int startIndex = min(index1, index2);
    int endIndex = max(index1, index2);

    vector<string> result;
    for (int i = startIndex + 1; i < endIndex; ++i) {
        result.push_back(planets[i]);
    }

    return result;
}

