int x_ory(int n, int x, int y) {
    bool is_prime = true;
    if (n <= 1) {
        is_prime = false;
    } else if (n == 2) {
        is_prime = true;
    } else if (n % 2 == 0) {
        is_prime = false;
    } else {
        for (int i = 3; i * i <= n; i += 2) {
            if (n % i == 0) {
                is_prime = false;
                break;
            }
        }
    }
    return is_prime ? x : y;
}

