using namespace std;

vector<int> even_odd_count(int num) {
    vector<int> counts(2, 0); // [even, odd]

    if (num == 0) {
        counts[0] = 1;
        return counts;
    }

    int n = abs(num);

    while (n > 0) {
        int digit = n % 10;
        if (digit % 2 == 0) {
            counts[0]++;
        } else {
            counts[1]++;
        }
        n /= 10;
    }

    return counts;
}

