string Strongest_Extension(string class_name, vector<string> extensions) {
    int max_strength = INT_MIN;
    string selected_ext;

    for (auto &ext : extensions) {
        int cap = 0, sm = 0;
        for (char c : ext) {
            if (isupper(c)) {
                cap++;
            } else if (islower(c)) {
                sm++;
            }
        }
        int strength = cap - sm;

        if (strength > max_strength) {
            max_strength = strength;
            selected_ext = ext;
        }
    }

    return selected_ext;
}

