using namespace std;

string findMaxUniqueWord(vector<string> words) {
    int max_unique = 0;
    string best_word;

    for (auto &word : words) {
        unordered_set<char> chars(word.begin(), word.end());
        int current_unique = chars.size();

        if (current_unique > max_unique) {
            max_unique = current_unique;
            best_word = word;
        } else if (current_unique == max_unique) {
            if (word < best_word) {
                best_word = word;
            }
        }
    }

    return best_word;
}

