using namespace std;

int evaluatePostfix(const vector<string>& postfix) {
    stack<int> eval_stack;
    for (const string& token : postfix) {
        if (isdigit(token[0]) || (token.size() > 1 && token[0] == '-' && isdigit(token[1]))) {
            int num = stoi(token);
            eval_stack.push(num);
        } else {
            int b = eval_stack.top(); eval_stack.pop();
            int a = eval_stack.top(); eval_stack.pop();
            if (token == "+") {
                eval_stack.push(a + b);
            } else if (token == "-") {
                eval_stack.push(a - b);
            } else if (token == "*") {
                eval_stack.push(a * b);
            } else if (token == "/") {
                // Handle division, assuming integer division truncates towards zero
                eval_stack.push(a / b);
            }
        }
    }
    return eval_stack.top();
}

vector<string> infixToPostfix(const vector<int>& operands, const vector<string>& operators) {
    vector<string> postfix;
    stack<string> op_stack;

    for (size_t i = 0; i < operands.size(); ++i) {
        // Add the operand to postfix
        postfix.push_back(to_string(operands[i]));
        if (i < operators.size()) { // Only push operator if there is one left
            string op = operators[i];
            while (!op_stack.empty() && op_stack.top() != "(") {
                // Check precedence: assume all operators have same precedence, but in reality,
                // we need to handle different precedences. For this problem, assuming all are same.
                postfix.push_back(op_stack.top());
                op_stack.pop();
            }
            op_stack.push(op);
        }
    }

    // Pop any remaining operators from the stack
    while (!op_stack.empty()) {
        postfix.push_back(op_stack.top());
        op_stack.pop();
    }

    return postfix;
}


